import axios from 'axios';

const API_BASE_URL = 'http://20.244.56.144/test/companies/AMZ/categories/Laptop/products?top=10&minPrice=1&maxPrice=10000'; // Replace with actual API base URL
const AUTH_TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..gWU9fzIfFM5oWBybgu1oZ-HkaScl_ibxoXhiUtOg51M'; // Replace with your actual auth token

const apiClient = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Authorization': `Bearer ${AUTH_TOKEN}`
    }
});

export const fetchProducts = async (company, category, top, minPrice, maxPrice) => {
    try {
        const response = await apiClient.get(`/companies/${company}/categories/${category}/products`, {
            params: {
                top,
                minPrice,
                maxPrice
            }
        });
        return response.data;
    } catch (error) {
        console.error('Error fetching products', error);
        return [];
    }
};

export const fetchProductById = async (id) => {
    try {
        const response = await apiClient.get(`/products/${id}`);
        return response.data;
    } catch (error) {
        console.error('Error fetching product', error);
        return null;
    }
};
