import React, { useEffect, useState } from 'react';
import { fetchProducts } from '../services/api';
import ProductList from '../components/ProductList';
import Filters from '../components/Filters';

const AllProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [companies, setCompanies] = useState([]);

    useEffect(() => {
        const getProducts = async () => {
            const data = await fetchProducts();
            setProducts(data);
            setFilteredProducts(data);

            const uniqueCategories = [...new Set(data.map(product => product.category))];
            const uniqueCompanies = [...new Set(data.map(product => product.company))];
            setCategories(uniqueCategories);
            setCompanies(uniqueCompanies);
        };

        getProducts();
    }, []);

    const handleFilterChange = (name, value) => {
        let filtered = [...products];

        if (name === 'category' && value) {
            filtered = filtered.filter(product => product.category === value);
        }

        if (name === 'company' && value) {
            filtered = filtered.filter(product => product.company === value);
        }

        if (name === 'availability') {
            filtered = filtered.filter(product => product.available === (value === 'true'));
        }

        if (name === 'minPrice' && value) {
            filtered = filtered.filter(product => product.price >= value);
        }

        if (name === 'maxPrice' && value) {
            filtered = filtered.filter(product => product.price <= value);
        }

        if (name === 'sort') {
            filtered.sort((a, b) => {
                if (value === 'price') return a.price - b.price;
                if (value === 'rating') return b.rating - a.rating;
                if (value === 'discount') return b.discount - a.discount;
                return 0;
            });
        }

        setFilteredProducts(filtered);
    };

    return (
        <div className="all-products-page">
            <Filters
                categories={categories}
                companies={companies}
                onFilterChange={handleFilterChange}
            />
            <ProductList products={filteredProducts} />
        </div>
    );
};

export default AllProductsPage;
